import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom'; // Certifique-se de importar isso
import styles from '../Styles/Login.module.css';
import Input from './Input';

export default function Login() {
    const [formValues, setFormValues] = useState({
        usuario: '',
        senha: ''
    });
    const [message, setMessage] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const { usuario, senha } = formValues;

        // Validação básica para o e-mail
        if (!usuario.includes('@') || !usuario.includes('.')) {
            setMessage('O campo usuário deve ser um email válido.');
            return;
        }

        if (!usuario || !senha) {
            setMessage('Por favor, preencha todos os campos.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('usuario', usuario);
            formData.append('senha', senha);

            const response = await fetch('http://localhost/login/login.php', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.error) {
                setMessage(result.message); // Atualiza o estado com a mensagem de erro
            } else {
                setMessage(result.message); // Atualiza o estado com a mensagem de sucesso
                setTimeout(() => {
                    navigate('/logado'); // Redireciona após 2 segundos para permitir a visualização da mensagem
                }, 2000);
            }
        } catch (error) {
            setMessage('Erro ao tentar fazer login.');
        }
    };

    const handleChange = (e) => {
        setFormValues({
            ...formValues,
            [e.target.name]: e.target.value
        });
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <div className={styles.container}>
            <div className={styles.login}>
                <h1>Entrar</h1>
                <form onSubmit={handleSubmit}>
                    <Input
                        name="usuario"
                        type="text"
                        placeholder="Usuário ou email"
                        value={formValues.usuario}
                        onChange={handleChange}
                    />

                    <div className={styles.password}>
                        <Input
                            name="senha"
                            type={showPassword ? 'text' : 'password'}
                            placeholder="Senha"
                            value={formValues.senha}
                            onChange={handleChange}
                        />

                        <FontAwesomeIcon
                            icon={showPassword ? faEyeSlash : faEye}
                            className={styles.icon}
                            onClick={togglePasswordVisibility}
                        />
                    </div>
                    <div className={styles.container2}>
                        <a href='#'>Esqueci minha senha</a>
                        <button type="submit">Entrar</button>
                    </div>
                </form>
                {message && <p className={styles.message}>{message}</p>} {/* Exibe a mensagem */}
            </div>
        </div>
    );
}
