import React, { useState } from 'react';
import styles from '../Styles/Register.module.css';
import Input from './Input';

export default function Register() {
    const [formData, setFormData] = useState({
        usuario: '',
        email: '',
        senha: '',
        confirmarSenha: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validar se as senhas coincidem
        if (formData.senha !== formData.confirmarSenha) {
            alert('As senhas não coincidem');
            return;
        }

        try {
            const response = await fetch('http://localhost/login/api.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
                body: new URLSearchParams(formData).toString()
            });

            const result = await response.text();
            alert(result); // Mensagem retornada pelo PHP
        } catch (error) {
            console.error('Erro:', error);
        }
    };

    return (
        <div className={styles.container}>
            <h1>Cadastrar</h1>
            <form onSubmit={handleSubmit}>
                <Input name="usuario" type="text" placeholder="Usuário" onChange={handleChange} />
                <Input name="email" type="text" placeholder="Email" onChange={handleChange} />
                <Input name="senha" type="password" placeholder="Senha" onChange={handleChange} />
                <Input name="confirmarSenha" type="password" placeholder="Confirmar senha" onChange={handleChange} />
                <button type="submit">Cadastrar</button>
            </form>
        </div>
    );
}
