import { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'
import styles from "../Styles/Login.module.css"
import Input from './Input'

export default function Login(){
    const [formValues, setFormValues] = useState({
        usuario: '',
        senha: ''
    });

    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const { usuario, senha } = formValues;

        if (!usuario || !senha) {
            alert('Por favor, preencha todos os campos.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('usuario', usuario);
            formData.append('senha', senha);

            const response = await fetch('http://localhost/login/login.php', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.error) {
                alert(result.error);
            } else {
                alert('Login realizado com sucesso!');
                // Armazene o token ou redirecione o usuário
                navigate('/dashboard'); // Redireciona para o dashboard, por exemplo
            }
        } catch (error) {
            alert('Erro ao tentar fazer login.');
        }
    };

    const handleChange = (e) => {
        setFormValues({
            ...formValues,
            [e.target.name]: e.target.value
        });
    };
    return(
        <div className={styles.container}>
            <div className={styles.login}>
                <h1>Entrar</h1>
                <Input name="usuario" type="text" placeholder="Usuário ou email"></Input>

                <div className={styles.password}>
                    <Input
                        name="senha"
                        type={showPassword ? 'text' : 'password'}
                        placeholder="Senha"
                    />

                    <FontAwesomeIcon
                        icon={showPassword ? faEyeSlash : faEye}
                        className={styles.icon}
                        onClick={togglePasswordVisibility}
                    />
                </div>
                <div className={styles.container2}>
                    <a href='#'>Esqueci minha senha</a>
                    <button>Entrar</button> 
                </div>                   
                
            </div>
        </div>
    )
}
