// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost/meu_projeto/api.php') // URL da API
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Houve um erro!', error);
      });
  }, []);

  return (
    <div>
      <h1>Usuários</h1>
      <ul>
        {data.map((item) => (
          <li key={item.id}>
            <strong>Usuário:</strong> {item.usuario}<br />
            <strong>Email:</strong> {item.email}<br />
            {/* Senha não deve ser exibida por questões de segurança */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
