import { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'
import styles from "../Styles/Login.module.css"
import Input from './Input'

function showpassword(){
    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
      setShowPassword(prevState => !prevState);}
}

export default function Login(){
    return(
        <div className={styles.container}>
            <div className={styles.login}>
                <h1>Entrar</h1>
                <Input name="usuario" type="text" placeholder="UsÃºario, email ou telefone"></Input>
                <div className={styles.password}><Input name="senha" type={showPassword ? 'text' : 'password'} placeholder="Senha"></Input><FontAwesomeIcon icon={faEye} className={styles.icon} onClick={showpassword} />
                <FontAwesomeIcon icon={faEyeSlash} className={`${styles.icon} ${styles.noshow}`}/></div>
                <section className={styles.container2}>
                    <a>Esqueci minha senha</a>
                    
                </section>
            </div>
        </div>
    )
}
